import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

// Create axios instance with default config
const axiosInstance = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json'
    },
    timeout: 10000 // 10 seconds
});

// Request interceptor (optional - for adding auth token, logging, etc.)
axiosInstance.interceptors.request.use(
    (config) => {
        // You can add auth token here
        // const token = localStorage.getItem('token');
        // if (token) {
        //     config.headers.Authorization = `Bearer ${token}`;
        // }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Response interceptor (optional - for error handling)
axiosInstance.interceptors.response.use(
    (response) => {
        return response;
    },
    (error) => {
        // Handle common errors here
        if (error.response?.status === 401) {
            // Redirect to login or refresh token
            console.log('Unauthorized - redirecting to login');
        }
        return Promise.reject(error);
    }
);

// Generic API helper functions
export const apiHelper = {
    // GET all items from endpoint
    getAll: async (endpoint) => {
        try {
            const response = await axiosInstance.get(endpoint);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: `Failed to fetch ${endpoint}` };
        }
    },

    // GET single item by id
    getById: async (endpoint, id) => {
        try {
            const response = await axiosInstance.get(`${endpoint}/${id}`);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: `Failed to fetch item ${id}` };
        }
    },

    // POST create new item
    create: async (endpoint, data) => {
        try {
            const response = await axiosInstance.post(endpoint, data);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: `Failed to create ${endpoint}` };
        }
    },

    // PUT update item by id
    update: async (endpoint, id, data) => {
        try {
            const response = await axiosInstance.put(`${endpoint}/${id}`, data);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: `Failed to update item ${id}` };
        }
    },

    // PATCH partial update
    patch: async (endpoint, id, data) => {
        try {
            const response = await axiosInstance.patch(`${endpoint}/${id}`, data);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: `Failed to patch item ${id}` };
        }
    },

    // DELETE item by id
    delete: async (endpoint, id) => {
        try {
            await axiosInstance.delete(`${endpoint}/${id}`);
        } catch (error) {
            throw error.response?.data || { message: `Failed to delete item ${id}` };
        }
    }
};

export default axiosInstance;
