import axios from './axios';

// API cho sản phẩm
const productAPI = {
    // Lấy tất cả sản phẩm
    getAll: async () => {
        try {
            const response = await axios.get('/products');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Không thể tải sản phẩm' };
        }
    },

    // Lấy 1 sản phẩm theo id
    getById: async (id) => {
        try {
            const response = await axios.get(`/products/${id}`);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Không tìm thấy sản phẩm' };
        }
    },

    // Thêm sản phẩm mới
    create: async (product) => {
        try {
            const response = await axios.post('/products', product);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Không thể thêm sản phẩm' };
        }
    },

    // Cập nhật sản phẩm
    update: async (id, product) => {
        try {
            const response = await axios.put(`/products/${id}`, product);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Không thể cập nhật sản phẩm' };
        }
    },

    // Xóa sản phẩm
    delete: async (id) => {
        try {
            await axios.delete(`/products/${id}`);
        } catch (error) {
            throw error.response?.data || { message: 'Không thể xóa sản phẩm' };
        }
    }
};

export default productAPI;
