import { apiHelper } from './axios';

const ENDPOINT = '/products';

// Products API Service
const productAPI = {
    // Get all products
    getAll: async () => {
        return await apiHelper.getAll(ENDPOINT);
    },

    // Get single product by id
    getById: async (id) => {
        return await apiHelper.getById(ENDPOINT, id);
    },

    // Create new product
    create: async (productData) => {
        return await apiHelper.create(ENDPOINT, productData);
    },

    // Update product
    update: async (id, productData) => {
        return await apiHelper.update(ENDPOINT, id, productData);
    },

    // Delete product
    delete: async (id) => {
        return await apiHelper.delete(ENDPOINT, id);
    },

    // Custom methods specific to products (if needed)
    searchByName: async (name) => {
        return await apiHelper.getAll(`${ENDPOINT}/search?name=${name}`);
    },

    getByPriceRange: async (minPrice, maxPrice) => {
        return await apiHelper.getAll(`${ENDPOINT}/price-range?min=${minPrice}&max=${maxPrice}`);
    }
};

export default productAPI;
