import axios from 'axios';

// Địa chỉ backend API
const API_URL = 'http://localhost:8080/api';

// Tạo axios instance
const axiosInstance = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json'
    }
});

export default axiosInstance;
