import { apiHelper } from './axios';

const ENDPOINT = '/auth';

// Authentication API Service
const authAPI = {
    // Login
    login: async (username, password) => {
        return await apiHelper.create(`${ENDPOINT}/login`, { username, password });
    },

    // Logout (if backend has endpoint)
    logout: async () => {
        return await apiHelper.create(`${ENDPOINT}/logout`, {});
    },

    // Register (if needed)
    register: async (userData) => {
        return await apiHelper.create(`${ENDPOINT}/register`, userData);
    },

    // Verify token (if needed)
    verifyToken: async (token) => {
        return await apiHelper.create(`${ENDPOINT}/verify`, { token });
    }
};

export default authAPI;
