import axios from 'axios';

// Địa chỉ backend API
const API_URL = 'http://localhost:8080/api';

// Tạo axios instance
const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json'
    }
});

// ==========================================
// API ĐĂNG NHẬP
// ==========================================
export const login = async (username, password) => {
    try {
        const response = await api.post('/auth/login', { username, password });
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Đăng nhập thất bại' };
    }
};

// ==========================================
// API SẢN PHẨM
// ==========================================

// Lấy tất cả sản phẩm
export const getProducts = async () => {
    try {
        const response = await api.get('/products');
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không thể tải sản phẩm' };
    }
};

// Lấy 1 sản phẩm theo id
export const getProduct = async (id) => {
    try {
        const response = await api.get(`/products/${id}`);
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không tìm thấy sản phẩm' };
    }
};

// Thêm sản phẩm mới
export const createProduct = async (product) => {
    try {
        const response = await api.post('/products', product);
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không thể thêm sản phẩm' };
    }
};

// Cập nhật sản phẩm
export const updateProduct = async (id, product) => {
    try {
        const response = await api.put(`/products/${id}`, product);
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không thể cập nhật sản phẩm' };
    }
};

// Xóa sản phẩm
export const deleteProduct = async (id) => {
    try {
        await api.delete(`/products/${id}`);
    } catch (error) {
        throw error.response?.data || { message: 'Không thể xóa sản phẩm' };
    }
};

// ==========================================
// MUỐN THÊM API KHÁC (VD: CATEGORIES)
// ==========================================
// Copy đoạn code bên dưới và thay đổi:

// Lấy tất cả categories
export const getCategories = async () => {
    try {
        const response = await api.get('/categories');
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không thể tải danh mục' };
    }
};

// Thêm category mới
export const createCategory = async (category) => {
    try {
        const response = await api.post('/categories', category);
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không thể thêm danh mục' };
    }
};

// Cập nhật category
export const updateCategory = async (id, category) => {
    try {
        const response = await api.put(`/categories/${id}`, category);
        return response.data;
    } catch (error) {
        throw error.response?.data || { message: 'Không thể cập nhật danh mục' };
    }
};

// Xóa category
export const deleteCategory = async (id) => {
    try {
        await api.delete(`/categories/${id}`);
    } catch (error) {
        throw error.response?.data || { message: 'Không thể xóa danh mục' };
    }
};
